import React, { useState, useEffect } from 'react';
import Countdown from 'react-countdown';
import './App.css';

const cleaningItems = [
  'トイレの床', 'トイレの便座の裏', '部屋全体', 'ベットと壁の間', 'ベットメイキング',
  'テレビの裏', 'ゲーム機全体', 'キッチン全体', 'キッチンのゴミ箱の中', '机の下',
  '机の上', '階段', '玄関内側', '下駄箱内', '玄関外側',
  '冷蔵庫の中', 'プロジェクターの部品が揃っている', '押し入れの中', 'クローゼットの扉が閉まっている', 'クローゼットの中',
  '水が配置してある', 'トイレットペーパが十分', 'ティッシュが配置してある', '食器棚', 'キッチンの引き出し',
  'コップが洗ってある', 'シンクに生ゴミがない', 'リビングの床', '部屋の右奥の角（床）', '椅子が揃っている',
];

const properties = ['物件A', '物件B', '物件C', '物件D'];

function App() {
  const [selectedItems, setSelectedItems] = useState([]);
  const [photos, setPhotos] = useState({});
  const [timerStarted, setTimerStarted] = useState(false);
  const [selectedProperty, setSelectedProperty] = useState('');

  useEffect(() => {
    const today = new Date();
    const dateString = `${today.getFullYear()}-${today.getMonth() + 1}-${today.getDate()}`;
    document.getElementById('current-date').textContent = dateString;
  }, []);

  const startChecklist = () => {
    const shuffled = cleaningItems.sort(() => 0.5 - Math.random());
    setSelectedItems(shuffled.slice(0, 5));
    setPhotos({});
    setTimerStarted(true);
  };

  const handlePhotoUpload = (index, event) => {
    const file = event.target.files[0];
    setPhotos(prevPhotos => ({ ...prevPhotos, [index]: URL.createObjectURL(file) }));
  };

  const handlePropertyChange = (event) => {
    setSelectedProperty(event.target.value);
  };

  const renderer = ({ minutes, seconds, completed }) => {
    if (completed) {
      return <span>タイムオーバー</span>;
    } else {
      return <span>{minutes}:{seconds}</span>;
    }
  };

  return (
    <div className="App">
      <h1>清掃チェックリスト</h1>
      <div>今日の日付: <span id="current-date"></span></div>
      <div>
        <label htmlFor="property-select">物件を選択:</label>
        <select id="property-select" value={selectedProperty} onChange={handlePropertyChange}>
          <option value="">選択してください</option>
          {properties.map((property, index) => (
            <option key={index} value={property}>{property}</option>
          ))}
        </select>
      </div>
      <button onClick={startChecklist}>チェック項目を表示</button>
      {timerStarted && (
        <Countdown date={Date.now() + 300000} renderer={renderer} />
      )}
      <ul>
        {selectedItems.map((item, index) => (
          <li key={index}>
            {item}
            <input
              type="file"
              accept="image/*"
              onChange={(event) => handlePhotoUpload(index, event)}
            />
            {photos[index] && <img src={photos[index]} alt={`清掃項目 ${index + 1}`} />}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
