import React, { useState } from 'react';
import './App.css';
import QRcode from ''

const App = () => {
  const [inputValue, setInputValue] = useState('');
  const [showDialog, setShowDialog] = useState(false);
  const [isDisplay , setIsDisplay] = useState(false);

  const handleInputChange = (e) => {
    setInputValue(e.target.value);
  };

  const handleButtonClick = () => {
    if (inputValue === 'abc123') {
      setShowDialog(true);
    } else {
      window.alert('入力された値が異なります。');
    }
  };

  const handleOkClick = () => {
    setIsDisplay(true)
    setShowDialog(false);
  };

  const handleCancelClick = () => {
    setShowDialog(false);
  };

  return (
    <div className="container">
      {isDisplay && (
        <img src="./images/QR_to20240423.png" />
      )}
      <input
        type="text"
        value={inputValue}
        onChange={handleInputChange}
        placeholder="入力してください"
        className="input"
      />
      <button onClick={handleButtonClick} className="button">確認</button>

      {showDialog && (
        <div className="dialog">
          <p>表示します</p>
          <button onClick={handleOkClick} className="dialogButton">OK</button>
          <button onClick={handleCancelClick} className="dialogButton">キャンセル</button>
        </div>
      )}
    </div>
  );
};

export default App;
